import io from 'socket.io-client';
import constants from './constants';

let socket;
  const token = sessionStorage['authToken']
  if(token)
  socket = io.connect(constants.backendUrl, {
    'query': 'token=' + sessionStorage['authToken']
  });

export default socket;
