import io from 'socket.io-client';
import constants from './constants';

let socket;
  const token = sessionStorage['authToken']
  if(token)
  socket = io.connect(constants.backendUrl, {
    'query': 'token=' + token
  });
  const emit = () => {}
  const on = () => {}

  export default socket ? socket : {emit: emit(), on: on()};
