    // }, [])

    if (alerts === [] || alerts === undefined)
        return (
            <div></div>
        )
    else
        return (
            <div id='alert-container'>
                {/* itterates over all the massages in state and creates a message component for them*/}
                { alerts.map( alert => 
                    <AlertRenderer key={alert.id} alert={alert} />
                ) }
            </div>
        )
}
